import { NavLink, useLocation } from "react-router-dom";
import { useState } from "react";

function Sidebar() {
  const location = useLocation();
  const [invoiceOpen, setInvoiceOpen] = useState(false);

  const isActive = (path) => location.pathname === path;
  const isHistoryActive = location.pathname.startsWith("/history");

  // Open invoice menu automatically if on a /history route
  const isInvoiceOpen = invoiceOpen || isHistoryActive;

  return (
    <nav id="sidebar" className="sidebar-wrapper">
      <div className="shop-profile">
        <p className="mb-1 fw-bold text-primary">Walmart</p>
        <p className="m-0">Los Angeles, California</p>
      </div>

      <div className="sidebarMenuScroll">
        <ul className="sidebar-menu">
          <li className={isActive("/") ? "active current-page" : ""}>
            <NavLink to="/">
              <i className="bi bi-pie-chart"></i>
              <span className="menu-text">Dashboard</span>
            </NavLink>
          </li>

          {/* Other links ... */}

          <li className={`treeview ${isInvoiceOpen ? "active current-page open" : ""}`}>
            {/* Toggle submenu on click */}
            <div
              className="treeview-title"
              onClick={() => setInvoiceOpen(!invoiceOpen)}
              style={{ cursor: "pointer" }}
            >
              <i className="bi bi-window-sidebar"></i>
              <span className="menu-text">Invoices</span>
            </div>

            <ul className="treeview-menu">
              <li>
                <NavLink to="/history/create" className={({ isActive }) => isActive ? "active-sub" : ""}>
                  Create Invoice
                </NavLink>
              </li>
              <li>
                <NavLink to="/history/view" className={({ isActive }) => isActive ? "active-sub" : ""}>
                  View Invoice
                </NavLink>
              </li>
              <li>
                <NavLink to="/history/list" className={({ isActive }) => isActive ? "active-sub" : ""}>
                  Invoice List
                </NavLink>
              </li>
            </ul>
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Sidebar;
